material.fragmentShader:
uniform float mNear;
uniform float mFar;

varying float vViewZDepth;

// INSERT DEFINES

void main() {

	float color = 1.0 - smoothstep( mNear, mFar, vViewZDepth );
	gl_FragColor = vec4( vec3( color ), 1.0 );
	vec4 diffuseColor = gl_FragColor;

	// INSERT BODY

	gl_FragColor.a = diffuseColor.a;
}
